package com.olivejua.trie;

import org.junit.Before;
import org.junit.Test;

import static org.assertj.core.api.Assertions.assertThat;

public class ImplementTrieTest {
    private Trie trie;

    @Before
    public void setUp() throws Exception {
        trie = new ImplementTrie();
    }

    @Test
    public void test1() {
        trie.insert("apple");
        trie.insert("apply");
        trie.insert("appear");
        assertThat(trie.startsWith("app")).isEqualTo(true);
        assertThat(trie.startsWith("appe")).isEqualTo(true);
        assertThat(trie.startsWith("d")).isEqualTo(false);
        assertThat(trie.search("apple")).isEqualTo(true);
        assertThat(trie.search("apply")).isEqualTo(true);
        assertThat(trie.search("applel")).isEqualTo(false);
        assertThat(trie.search("appe")).isEqualTo(false);
        System.out.println();
    }

    @Test
    public void test2() {
        trie.insert("");
        trie.insert("");
        trie.insert("");
        assertThat(trie.startsWith("")).isEqualTo(true);
        System.out.println();
    }
}